function createModal(n){$(".modal-overlay").length===0&&$("body").prepend('<div tabindex="0" class="cbm-modal-overlay"><\/div>');var t=$(n);t.addClass("cbm-modal");t.attr("tabindex",0);t.attr("aria-modal","true");showModal(n);trapFocusInModal(n)}function trapFocusInModal(n){var t=$(n),i=t.find("select, input, textarea, button, a").filter(":visible");$(".cbm-modal-overlay").focus(function(){i.length<1?setTimeout(function(){t.focus()},20):setTimeout(function(){i.first().focus()},20)});t.focus()}function showModal(n){$(n).show();$(".cbm-modal-overlay").show()}function hideModal(n){$(n).hide();$(".cbm-modal-overlay").hide()}